import React from 'react';
import { Radio, Switch, Row, Col} from 'antd';
import {ShotChart} from './ShotChart';
import { CountSlider } from './CountSlider';

const RadioGroup = Radio.Group;

export class DataViewContainer extends React.Component {
    state = {
        minCount: 2,
        chartType: 'hexbin',
        displayToolTips: true
    }

    onCountSliderChange = (minCount) => {
        this.setState({
            minCount
        });
    }

    onChartTypeChange = (e) => {
        this.setState ({
            chartType: e.target.value
        });

    }

    onTooltipChange = (displayToolTips) => {
        this.setState({
            displayToolTips
        });
    }

    render() {
        const { minCount, chartType, displayToolTips } = this.state;
        console.log(this.state);

        return (
            <div className="data-view">
                <ShotChart className={"data-view"}
                           playerId={this.props.playerId}
                           minCount={minCount}
                           displayToolTips={displayToolTips}
                           chartType={chartType}

                />

                {
                    chartType === "hexbin" ?
                        <CountSlider
                            value={minCount}
                            onChange={this.onCountSliderChange}
                        /> : null
                }

                <Row>
                    <Col span={8} offset={7}>
                        <RadioGroup onChange={this.onChartTypeChange} value={chartType}>
                            <Radio value={"hexbin"}>Hexbin</Radio>
                            <Radio value={"scatter"}>Scatter</Radio>

                        </RadioGroup>
                    </Col>

                    <Col span={2}>
                        <Switch
                            checkedChildren="On"
                            unCheckedChildren="Off"
                            defaultChecked
                            onChange={this.onTooltipChange}
                        />
                    </Col>
                </Row>

            </div>
        );
    }
}
