import React from 'react';
import { Profile } from './Profile.js';
import nba from 'nba';

window.nba = nba;

export class Main extends React.Component {

    state = {
        playerInfo: {
            playerId: 201939
        }
    }
    componentDidMount() {
        const { playerId } = nba.findPlayer('Stephen Curry').playerId;


        nba.stats.playerInfo({PlayerID: playerId}).then(
            (info) => {
                console.log(info);
                const commonPlayerInfo = info.commonPlayerInfo[0];
                const playerHeadlineStats = info.playerHeadlinesStats[0];
                const playerInfo = {
                    ...commonPlayerInfo,
                    ...playerHeadlineStats
                };

                this.setState({
                    playerInfo
                })
            }

        );

    }

    render() {
        return (
            <div className="main">
                <Profile />
                <div >ShotChart</div>
            </div>
        );
    }
}
